import * as React from "react";
import Box from "@mui/material/Box";
import Card from "@mui/material/Card";
import up from "../assets/BNM_Metric_Up.svg";
import down from "../assets/BNM_Metric_Down.svg";
import CardActions from "@mui/material/CardActions";
import CardContent from "@mui/material/CardContent";
import Button from "@mui/material/Button";
import Typography from "@mui/material/Typography";

const bull = (
  <Box
    component="span"
    sx={{ display: "inline-block", mx: "2px", transform: "scale(0.8)" }}
  >
    â€¢
  </Box>
);

const data = {
  "Metric Name": "Sessions (Site Traffic)",
  "Metric Count": "1373",
  "Week-Over-Week Change": "-2.00%",
  Percentile: "100th",
};

const card = (
  // <React.Fragment>
  //   <CardContent>
  //     <Typography sx={{ fontSize: 14 }} color="text.secondary" gutterBottom>
  //       Word of the Day
  //     </Typography>
  //     <Typography variant="h5" component="div">
  //       be{bull}nev{bull}o{bull}lent
  //     </Typography>
  //     <Typography sx={{ mb: 1.5 }} color="text.secondary">
  //       adjective
  //     </Typography>
  //     <Typography variant="body2">
  //       well meaning and kindly.
  //       <br />
  //       {'"a benevolent smile"'}
  //     </Typography>
  //   </CardContent>
  //   <CardActions>
  //     <Button size="small">Learn More</Button>
  //   </CardActions>
  // </React.Fragment>
  <div className="card">
    <h3>{data["Metric Name"]}</h3>
    <p className="figures">
      {data["Metric Count"]} |
      <span className="percentile" id="overview-percentile">
        {data[0]["Percentile"].match(/(\d+)/)[0]}%
        <span style={{ display: "none", flexDirection: "column" }}>
          <img src={down} style={{ width: "15px" }} />
          <p style={{ fontSize: "12px", color: "black" }}>pctl</p>
        </span>
        <span style={{ display: "none", flexDirection: "column" }}>
          <img src={up} style={{ width: "15px" }} />
          <p style={{ fontSize: "12px", color: "black" }}>pctl</p>
        </span>
      </span>
    </p>
    <p className="perf">
      Your performance from last week
      <span className="wow" id="overview-wow">
        {checkFigure(
          data["Week-Over-Week Change"].match(/-?\d+/g)[0],
          "overview-wow",
          "overview-percentile"
        ) + "%"}
      </span>
    </p>
  </div>
);

const checkFigure = (figure, wow_id, percentile_id) => {
  let div = document.createElement("span");
  div.className = "indicator";
  if (figure < 0) {
    const wow_el = document.getElementById(wow_id);
    const percentile_el = document.getElementById(percentile_id);
    if (wow_el && percentile_el) {
      wow_el.classList += " negative";
      percentile_el.classList += " negative";

      if (percentile_el.childNodes[2]) {
        percentile_el.childNodes[2].style.display = "flex";
      }
    }
  } else {
    const wow_el = document.getElementById(wow_id);
    const percentile_el = document.getElementById(percentile_id);
    if (wow_el && percentile_el) {
      wow_el.classList += " positive";
      percentile_el.classList += " positive";

      if (percentile_el.childNodes[3]) {
        percentile_el.childNodes[3].style.display = "flex";
      }
    }
  }
  return figure;
};

export default function MetricCard() {
  return (
    <Box sx={{ minWidth: 275 }}>
      <Card variant="outlined">{card}</Card>
    </Box>
  );
}
