import React, { useState, useEffect } from "react";
import MetricAppBar from "./components/MetricAppBar";
import MenuBar from "./components/MenuBar";
import OverviewPage from "./screens/OverviewPage";
import TrafficPage from "./screens/TrafficPage";
import SitePerformancePage from "./screens/SitePerformancePage";

function App() {
  const data = [
    {
      "Metric Name": "Sessions (Site Traffic)",
      "Metric Count": "1373",
      "Week-Over-Week Change": "-2.00%",
      Percentile: "100th",
    },
  ];
  const [activeTab, setActiveTab] = useState("tab1");

  const handleTabSelection = (selectedTab) => {
    setActiveTab(selectedTab);
  };

  const renderActiveSelection = () => {
    switch (activeTab) {
      case "tab1":
        return <OverviewPage data={data} />;
      case "tab2":
        return <TrafficPage />;
      case "tab3":
        return <SitePerformancePage />;
      default:
        return null;
    }
  };

  return (
    <div>
      <MetricAppBar />
      <MenuBar onClick={handleTabSelection} activeTab={activeTab} />
      {renderActiveSelection()}
    </div>
  );
}

export default App;
