import * as React from "react";
import Box from "@mui/material/Box";
import up from "../assets/BNM_Metric_Up.svg";
import down from "../assets/BNM_Metric_Down.svg";

const bull = (
  <Box
    component="span"
    sx={{ display: "inline-block", mx: "2px", transform: "scale(0.8)" }}
  >
    â€¢
  </Box>
);

const data = {
  "Metric Name": "Sessions (Site Traffic)",
  "Metric Count": "1373",
  "Week-Over-Week Change": "-2.00%",
  Percentile: "100th",
};

const isPositiveWowChange =
  Math.sign(data["Week-Over-Week Change"].match(/-?\d+/g)[0]) === 1;

export default function MetricCard() {
  return (
    <div className="card">
      <h3>{data["Metric Name"]}</h3>
      <p className="figures">
        {data["Metric Count"]} |
        <span
          className="percentile"
          style={{ color: !isPositiveWowChange && "red" }}
          id="overview-percentile"
        >
          {data["Percentile"].match(/(\d+)/)[0]}%
          <span style={{ display: "none", flexDirection: "column" }}>
            <img
              src={isPositiveWowChange ? up : down}
              alt={""}
              style={{ width: "15px" }}
            />
            <p style={{ fontSize: "12px", color: "black" }}>pctl</p>
          </span>
        </span>
      </p>
      <p className="perf">
        Your performance from last week
        <span
          className="wow"
          style={{ color: !isPositiveWowChange && "red" }}
          id="overview-wow"
        >
          {data["Week-Over-Week Change"].match(/-?\d+/g)[0] + "%"}
        </span>
      </p>
    </div>
    // <Box sx={{ minWidth: 275 }}>
    //   <Card variant="outlined">{card}</Card>
    // </Box>
  );
}
