import React, { useState } from "react";
import { QueryClient, QueryClientProvider } from "react-query";
import { createTheme, ThemeProvider } from "@mui/material/styles";
import MetricAppBar from "./components/MetricAppBar";
import MenuBar from "./components/MenuBar";
import MetricCards from "./screens/MetricCards";
import { useOverview, useTraffic, useSitePerformance } from "./hooks/metrics";
import "./App.css";

const queryClient = new QueryClient();

function App() {
  const overview = useOverview();
  const traffic = useTraffic();
  const sitePerformance = useSitePerformance();
  const theme = createTheme({
    palette: {
      primary: {
        main: "#83c676",
      },
      secondary: {
        main: "#000000",
      },
      button: {
        main: "#4e4d4f",
      },
    },
  });

  const data = [
    {
      "Metric Name": "Sessions (Site Traffic)",
      "Metric Count": "1373",
      "Week-Over-Week Change": "-2.00%",
      Percentile: "100th",
    },
    {
      "Metric Name": "Sessions (Site Traffic)",
      "Metric Count": "1373",
      "Week-Over-Week Change": "-2.00%",
      Percentile: "100th",
    },
    {
      "Metric Name": "Sessions (Site Traffic)",
      "Metric Count": "1373",
      "Week-Over-Week Change": "-2.00%",
      Percentile: "100th",
    },
    {
      "Metric Name": "Sessions (Site Traffic)",
      "Metric Count": "1373",
      "Week-Over-Week Change": "-2.00%",
      Percentile: "100th",
    },
  ];
  const [activeTab, setActiveTab] = useState("Overview");

  const handleTabSelection = (selectedTab) => {
    setActiveTab(selectedTab);
  };

  const renderActiveSelection = () => {
    switch (activeTab) {
      case "Overview":
        return <MetricCards data={overview.state} />;
      case "Traffic":
        return <MetricCards data={traffic.state} />;
      case "Site Performance":
        return <MetricCards data={sitePerformance.state} />;
      default:
        return null;
    }
  };

  return (
    <div>
      <QueryClientProvider client={queryClient}>
        <ThemeProvider theme={theme}>
          <MetricAppBar />
          <MenuBar onClick={handleTabSelection} activeTab={activeTab} />
          {renderActiveSelection()}
        </ThemeProvider>
      </QueryClientProvider>
    </div>
  );
}

export default App;
