import React, { useState, useEffect } from "react";
import { createTheme, ThemeProvider } from "@mui/material/styles";
import MetricAppBar from "./components/MetricAppBar";
import MenuBar from "./components/MenuBar";
import MetricCards from "./components/MetricCards";
import "./App.css";
import OverviewPage from "./screens/OverviewPage";
import TrafficPage from "./screens/TrafficPage";
import SitePerformancePage from "./screens/SitePerformancePage";

function App() {
  const theme = createTheme({
    palette: {
      primary: {
        main: "#83c676",
      },
      secondary: {
        main: "#000000",
      },
      button: {
        main: "#4e4d4f",
      },
    },
  });

  const data = [
    {
      "Metric Name": "Sessions (Site Traffic)",
      "Metric Count": "1373",
      "Week-Over-Week Change": "-2.00%",
      Percentile: "100th",
    },
  ];
  const [activeTab, setActiveTab] = useState("Overview");

  const handleTabSelection = (selectedTab) => {
    setActiveTab(selectedTab);
  };

  const renderActiveSelection = () => {
    switch (activeTab) {
      case "Overview":
        return <MetricCards data={data} />;
      case "tab2":
        return <MetricCards />;
      case "tab3":
        return <MetricCards />;
      default:
        return null;
    }
  };

  return (
    <div>
      <ThemeProvider theme={theme}>
        <MetricAppBar />
        <MenuBar onClick={handleTabSelection} activeTab={activeTab} />
        {renderActiveSelection()}
      </ThemeProvider>
    </div>
  );
}

export default App;
