  console.log({ isLoading, error, data });

  useEffect(() => {
    if (!isLoading) {
      setSitePerformance(data);
      setIsReady(true);
    }
  }, [data, isLoading]);

  return {
    state: sitePerformance,
    isLoading: !isReady,
    actions: {},
  };
}
